import React, {useContext, useEffect, useState} from 'react';
import {KeyboardAvoidingView, ScrollView, StyleSheet, View, Platform, TextInput,} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import {Avatar, Button, Input, ListItem, Text, Divider} from "react-native-elements";
import UserContext from "../../connection/userContext";
import {deleteUserData, getData, saveData} from "../../connection/AsyncStorage";
import {Firebase} from "../../connection/comms";
import firebase from "firebase";


export default function Measurements({navigation, route}) {

    let measurementsList = {
        arms: "Arms Length",
        shoulder: "Shoulders",
        chest: "Chest",
        stomach: "Stomach",
        waist: "Waist",
        armHole: "Arms Hole",
        shirtLength: "Shirt Length",
        collar: "Collar Size",
        pantsLength: "Pants Length",
        pantsWaist: "Pants Waist",
        crotch: "Crotch",
        cuffs: "Cuffs Size"
    }
    return (
        <UserContext.Consumer>
            {({loggedIn, setLoggedin}) => (
                <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : "padding"}
                                      style={styles.container} keyboardVerticalOffset={100}>
                    <Text style={{
                        fontSize: 25,
                        fontWeight: "bold",
                        paddingHorizontal: 20,
                        marginTop: 10
                    }}>{route.name}</Text>
                    <Text style={{fontSize: 14, fontWeight: "normal", paddingHorizontal: 20}}>Add your measurements
                        precisely. These will be shared with the tailors you hire. All measurements are recorded in
                        inches.</Text>
                    <ScrollView style={styles.container} contentContainerStyle={styles.contentContainer}>
                        {measurementsList.map((v, k) => {
                            console.log(v, k)
                        })}

                        <ListItem
                            topDivider
                            title="Arms Length (in)"
                            input={{
                                placeholder: '00.0',
                                defaultValue: loggedIn.measurements.armsLength,
                            }}
                        />


                        <Button
                            type={"outline"}
                            title={"Update Measurements"}
                            buttonStyle={{borderColor: "#ef3caf", borderRadius: 20}}
                            titleStyle={{color: "#ef3caf"}}
                            onPress={() => {
                                alert("Updated")
                            }}
                            style={{marginTop: 20, marginHorizontal: 20}}
                        />


                    </ScrollView>
                </KeyboardAvoidingView>

            )}
        </UserContext.Consumer>
    );
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    developmentModeText: {
        marginBottom: 20,
        color: 'rgba(0,0,0,0.4)',
        fontSize: 14,
        lineHeight: 19,
        textAlign: 'center',
    },
    contentContainer: {
        paddingTop: 30,
    },
    welcomeContainer: {
        alignItems: 'center',
        marginTop: 10,
        marginBottom: 20,
    },
    welcomeImage: {
        width: 100,
        height: 80,
        resizeMode: 'contain',
        marginTop: 3,
        marginLeft: -10,
    },
    getStartedContainer: {
        alignItems: 'center',
        marginHorizontal: 50,
    },
    homeScreenFilename: {
        marginVertical: 7,
    },
    codeHighlightText: {
        color: 'rgba(96,100,109, 0.8)',
    },
    codeHighlightContainer: {
        backgroundColor: 'rgba(0,0,0,0.05)',
        borderRadius: 3,
        paddingHorizontal: 4,
    },
    getStartedText: {
        fontSize: 17,
        color: 'rgba(96,100,109, 1)',
        lineHeight: 24,
        textAlign: 'center',
    },
    tabBarInfoContainer: {
        position: 'absolute',
        bottom: 0,
        left: 0,
        right: 0,
        ...Platform.select({
            ios: {
                shadowColor: 'black',
                shadowOffset: {width: 0, height: -3},
                shadowOpacity: 0.1,
                shadowRadius: 3,
            },
            android: {
                elevation: 20,
            },
        }),
        alignItems: 'center',
        backgroundColor: '#fbfbfb',
        paddingVertical: 20,
    },
    tabBarInfoText: {
        fontSize: 17,
        color: 'rgba(96,100,109, 1)',
        textAlign: 'center',
    },
    navigationFilename: {
        marginTop: 5,
    },
    helpContainer: {
        marginTop: 15,
        alignItems: 'center',
    },
    helpLink: {
        paddingVertical: 15,
    },
    helpLinkText: {
        fontSize: 14,
        color: '#2e78b7',
    }
});
