import React, {useContext, useEffect, useState} from 'react';
import {KeyboardAvoidingView, ScrollView, StyleSheet, View, Platform, TextInput,} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import {Avatar, Button, Input, ListItem, Text, Divider} from "react-native-elements";
import UserContext from "../../connection/userContext";
import {Firebase} from "../../connection/comms";
import {saveData} from "../../connection/AsyncStorage";


export default function AddNewService({navigation, route}) {
    const {loggedIn} = useContext(UserContext)

    const [service, setService] = useState({})

    useEffect(() => {
            //Image Permissions
            (async () => {
                if (Platform.OS !== 'web') {
                    const {status} = await ImagePicker.requestCameraRollPermissionsAsync();
                    if (status !== 'granted') {
                        alert('Sorry, we need camera roll permissions to make this work!');
                    }
                }
            })();

    }, [])
    console.log(service)

    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            allowsEditing: true,
            aspect: [1, 1],
            quality: 0.5,
        });

        console.log(result);

        if (!result.cancelled) {
            Firebase.serviceIMG(result.uri)
                .then(link => {
                    console.log("link, >>", link)
                    setService(previousState => ({...previousState, cover: link}));
                })

        }
    };

    return (
        <UserContext.Consumer>
            {({loggedIn, setLoggedin}) => (
                <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : "padding"}
                                      style={styles.container} keyboardVerticalOffset={100}>
                    <Text style={{
                        fontSize: 25,
                        fontWeight: "bold",
                        paddingHorizontal: 20,
                        marginTop: 10
                    }}>{route.name}</Text>
                    <ScrollView style={styles.container} contentContainerStyle={styles.contentContainer}>
                        <View style={{marginBottom: 20, display: "flex", justifyContent: "flex-start"}}>
                            <Avatar source={{uri: service.cover}} size={"medium"}
                                    title={loggedIn.name.charAt(0).toUpperCase()} showEditButton={true}
                                    onEditPress={() => pickImage()}/>
                            <Avatar source={{uri: service.cover}} size={"medium"}
                                    title={loggedIn.name.charAt(0).toUpperCase()} showEditButton={true}
                                    onEditPress={() => pickImage()}/>
                            <Avatar source={{uri: service.cover}} size={"medium"}
                                    title={loggedIn.name.charAt(0).toUpperCase()} showEditButton={true}
                                    onEditPress={() => pickImage()}/>
                            <Avatar source={{uri: service.cover}} size={"medium"}
                                    title={loggedIn.name.charAt(0).toUpperCase()} showEditButton={true}
                                    onEditPress={() => pickImage()}/>
                            <Avatar source={{uri: service.cover}} size={"medium"}
                                    title={loggedIn.name.charAt(0).toUpperCase()} showEditButton={true}
                                    onEditPress={() => pickImage()}/>
                        </View>
                        <ListItem
                            topDivider
                            title="I will..."
                            input={{
                                placeholder: 'do the task',
                                defaultValue: service.title,
                                onChangeText: (text) => {
                                    setService(previousState => ({...previousState, title: text}));
                                }
                            }}
                        />

                        <ListItem
                            topDivider
                            title="Starting at only, Rs: "
                            input={{
                                placeholder: 'Rs: 5000',
                                defaultValue: service.price,
                                onChangeText: (text) => {
                                    setService(previousState => ({...previousState, price: text}));
                                }
                            }}
                        />
                        <ListItem
                            topDivider
                            bottomDivider
                            title="Here are the details"
                            input={{
                                multiline: true,
                                placeholder: 'Rs: 5000',
                                defaultValue: service.details,
                                style: {height: "100%"},
                                containerStyle: {height: 100},
                                onChangeText: (text) => {
                                    setService(previousState => ({...previousState, details: text}));
                                }
                            }}
                        />

                        <Button
                            type={"outline"}
                            title={"Add Service"}
                            buttonStyle={{borderColor: "#ef3caf", borderRadius: 20}}
                            titleStyle={{color: "#ef3caf"}}
                            onPress={function () {
                                Firebase.addNewService(service, loggedIn.uid).then(r => {
                                    if (r){
                                        navigation.navigate("Services", {reload: true})
                                    }
                                })
                            }}
                            style={{marginTop: 20, marginHorizontal: 20}}
                        />
                        <Button
                            type={"solid"}
                            title={"Back to Profile"}
                            buttonStyle={{borderColor: "#797979", borderRadius: 20}}
                            titleStyle={{color: "#ffffff"}}
                            onPress={() => {
                                navigation.goBack()

                            }}
                            style={{marginTop: 20, marginHorizontal: 20}}
                        />


                    </ScrollView>
                </KeyboardAvoidingView>

            )}
        </UserContext.Consumer>
    );
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },

    contentContainer: {
        paddingTop: 30,
        paddingBottom: 20
    },

});
