import React, {useEffect, useState} from "react";
import {Platform, StatusBar, StyleSheet, View} from 'react-native';
import {NavigationContainer} from '@react-navigation/native';
import {createStackNavigator} from '@react-navigation/stack';
import BottomTabNavigator from './navigation/BottomTabNavigator';
import UserContext from "./connection/userContext";
import AuthNavigation from "./navigation/AuthNavigation";
import {getData, saveData} from "./connection/AsyncStorage";
import DetailsScreen from "./screens/DetailsScreen";

const Stack = createStackNavigator();

const App = () => {
    const [loggedIn, setLoggedin] = useState(undefined);

    useEffect(() => {
        if (!loggedIn) {
            getData().then(r => {
                console.log("Storage to state - App.Js Effect")
                setLoggedin(r)
            })
        }
    })


    const value = {loggedIn, setLoggedin};

    console.log("App.js Context Data",loggedIn)


    if (loggedIn) {
        //This is the Main App Stack
        return (
            <UserContext.Provider value={value}>
                <View style={styles.container}>
                    {Platform.OS === 'ios' && <StatusBar barStyle="default"/>}
                    <NavigationContainer>
                        <Stack.Navigator>
                            <Stack.Screen name="Root" component={BottomTabNavigator}/>
                            <Stack.Screen name="Details" component={DetailsScreen}/>
                        </Stack.Navigator>
                    </NavigationContainer>
                </View>
            </UserContext.Provider>
        );
    } else {
        return (
            //This is the Authentications screen stack.
            <UserContext.Provider value={value}>
                <UserContext.Consumer>
                    {({loggedIn, setLoggedin}) => (
                        <View style={styles.container}>
                            {Platform.OS === 'ios' && <StatusBar barStyle="default"/>}
                            <NavigationContainer>
                                <AuthNavigation/>
                            </NavigationContainer>
                        </View>
                    )}
                </UserContext.Consumer>
            </UserContext.Provider>

        );
    }

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
});

export default App;
