import React, {useEffect, useState} from 'react';
import {KeyboardAvoidingView, ScrollView, StyleSheet, View, Platform, TextInput} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import {Avatar, Button, ListItem, Text} from "react-native-elements";
import UserContext from "../connection/userContext";
import {deleteUserData, saveData} from "../connection/AsyncStorage";
import {Firebase} from "../connection/comms";
import Toast from "../components/ToastMessage";


export default function ProfileScreen() {
    let[dp, setdp] = useState(null)
    let[bio, setbio] = useState(null)

    useEffect(() => {
        (async () => {
            if (Platform.OS !== 'web') {
                const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
                if (status !== 'granted') {
                    alert('Sorry, we need camera roll permissions to make this work!');
                }
            }
        })();
    }, []);

    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.All,
            allowsEditing: true,
            aspect: [4, 3],
            quality: 1,
        });
        console.log(result);

        if (!result.cancelled) {
            setdp(result.uri);
        }
    };

    console.log(bio)

    return (
        <UserContext.Consumer>
            {({loggedIn, setLoggedin}) => (
                <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : "padding"}
                                      style={styles.container}>
                    {loggedIn.profileStatus === "incomplete" ? <Toast message={"Please Complete your profile."} color={"#ef3caf"}/>: null}

                    <ScrollView style={styles.container} contentContainerStyle={styles.contentContainer}>
                        <View style={{alignItems: "center", marginBottom: 20}}>
                            <Avatar source={"P"} size={"xlarge"} rounded title={loggedIn.name.charAt(0).toUpperCase()}
                                    showEditButton={true} onEditPress={()=>pickImage}/>
                        </View>
                        <View style={{alignItems: "center", marginBottom: 20}}>
                            <TextInput placeholder={"Write bio here (300 chars max)"} defaultValue={loggedIn.bio}
                                       maxLength={300} multiline={true} onChangeText={(e)=>setbio(e)}
                                       style={{border: 0, textAlign: "center", width:"80%", height: 100}}/>
                        </View>
                        <ListItem
                            topDivider
                            title="Full Name"
                            input={{
                                placeholder: 'Type Here',
                                defaultValue: loggedIn.name,
                                disabled: true
                            }}
                        />
                        <ListItem
                            topDivider
                            title="Email"
                            input={{
                                placeholder: 'Type Here',
                                defaultValue: loggedIn.email,
                                textContentType: "emailAddress",
                                disabled: true
                            }}
                        />
                        <ListItem
                            topDivider
                            title="Location"
                            input={{
                                placeholder: 'Type Here',
                                defaultValue: loggedIn.city,
                                onChangeText: text => {
                                    setLoggedin(previousState => ({...previousState, city: text}));
                                }
                            }}
                        />
                        <Button
                            onPress={() => {
                                Firebase.updateCity(loggedIn.city, loggedIn.uid).then(r => {
                                    if (r === true) {
                                        saveData(loggedIn)
                                    }
                                })
                            }}
                            title={"Update City"}
                            style={{padding: 10, marginTop: 20}}
                        />
                        <Button
                            onPress={() => {
                                deleteUserData().then(() => {
                                    setLoggedin(undefined)
                                    Firebase.signOut().then(() => console.log("Logged out"))
                                })
                            }}
                            title={"Sign Out"}
                            style={{padding: 10, marginTop: 20}}
                        />

                    </ScrollView>
                </KeyboardAvoidingView>

            )}
        </UserContext.Consumer>
    );
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    developmentModeText: {
        marginBottom: 20,
        color: 'rgba(0,0,0,0.4)',
        fontSize: 14,
        lineHeight: 19,
        textAlign: 'center',
    },
    contentContainer: {
        paddingTop: 30,
    },
    welcomeContainer: {
        alignItems: 'center',
        marginTop: 10,
        marginBottom: 20,
    },
    welcomeImage: {
        width: 100,
        height: 80,
        resizeMode: 'contain',
        marginTop: 3,
        marginLeft: -10,
    },
    getStartedContainer: {
        alignItems: 'center',
        marginHorizontal: 50,
    },
    homeScreenFilename: {
        marginVertical: 7,
    },
    codeHighlightText: {
        color: 'rgba(96,100,109, 0.8)',
    },
    codeHighlightContainer: {
        backgroundColor: 'rgba(0,0,0,0.05)',
        borderRadius: 3,
        paddingHorizontal: 4,
    },
    getStartedText: {
        fontSize: 17,
        color: 'rgba(96,100,109, 1)',
        lineHeight: 24,
        textAlign: 'center',
    },
    tabBarInfoContainer: {
        position: 'absolute',
        bottom: 0,
        left: 0,
        right: 0,
        ...Platform.select({
            ios: {
                shadowColor: 'black',
                shadowOffset: {width: 0, height: -3},
                shadowOpacity: 0.1,
                shadowRadius: 3,
            },
            android: {
                elevation: 20,
            },
        }),
        alignItems: 'center',
        backgroundColor: '#fbfbfb',
        paddingVertical: 20,
    },
    tabBarInfoText: {
        fontSize: 17,
        color: 'rgba(96,100,109, 1)',
        textAlign: 'center',
    },
    navigationFilename: {
        marginTop: 5,
    },
    helpContainer: {
        marginTop: 15,
        alignItems: 'center',
    },
    helpLink: {
        paddingVertical: 15,
    },
    helpLinkText: {
        fontSize: 14,
        color: '#2e78b7',
    }
});
