import React, {useContext, useEffect, useState} from 'react';
import {KeyboardAvoidingView, ScrollView, StyleSheet, View, Platform, TextInput,} from 'react-native';
import {Avatar, Button, Image, ListItem, Text} from "react-native-elements";
import UserContext from "../../connection/userContext";
import {Firebase} from "../../connection/comms";
import {Ionicons} from "@expo/vector-icons";
import {SliderBox} from "react-native-image-slider-box";


export default function ShowServiceDetails({navigation, route}) {
    console.log(route.params)

    return (
        <UserContext.Consumer>
            {({loggedIn, setLoggedin}) => (
                <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : "padding"}
                                      style={styles.container} keyboardVerticalOffset={100}>

                    <SliderBox images={route.params.service?.cover} sliderBoxHeight={300} dotStyle={{marginBottom: 20}}
                               dotColor={"red"}/>
                    <View style={{
                        flex: 1,
                        backgroundColor: 'rgba(255,255,255,1)',
                        borderTopLeftRadius: 20,
                        borderTopRightRadius: 20,
                        paddingHorizontal: 20,
                        marginTop: -20,
                    }}>
                        <View style={{
                            marginTop: -25,
                            justifyContent: "flex-end",
                            display: "flex",
                            flexDirection: "row",
                        }}>
                            {loggedIn.uid === route.params.service?.seller ?
                                <Button icon={<Ionicons name={"ios-trash"} size={28} color={"white"}/>}
                                        buttonStyle={{
                                            backgroundColor: "red",
                                            width: 50,
                                            height: 50,
                                            borderRadius: 50,
                                        }}
                                        onPress={() => (
                                            Firebase.deleteThisService(route.params.service.id).then(r => {
                                                if (r === true) {
                                                    navigation.navigate("Services", {reload: true})
                                                } else {
                                                    alert(r)
                                                }
                                            })
                                        )}
                                /> : <><Button icon={<Ionicons name={"ios-cart"} size={28} color={"white"}/>}
                                               title={" Purchase"}
                                               buttonStyle={{
                                                   backgroundColor: "#0085a2",
                                                   width: 150,
                                                   height: 50,
                                                   borderRadius: 50,
                                                   marginRight: 10,
                                               }}
                                />

                                    <Button icon={<Ionicons name={"ios-mail"} size={28} color={"white"}/>}
                                            title={" Chat"}
                                            buttonStyle={{
                                                backgroundColor: "#a362fd",
                                                width: 100,
                                                height: 50,
                                                borderRadius: 50,
                                            }}
                                            onPress={()=>navigation.navigate("Messages",{screen: "Chat", params: {contact: route.params.service.seller, title: "Chat with SELLER"}})}
                                            // onPress={()=>navigation.navigate("Chat",{contact: route.params.service.seller, title: "Chat with SELLER"})}
                                    />
                                </>}


                        </View>
                        <ScrollView style={styles.container} contentContainerStyle={styles.contentContainer}>

                            <Text style={{
                                fontSize: 24,
                                color: "grey",
                                fontWeight: "bold",
                            }}>{route.params.service?.title}</Text>
                            <Text style={{
                                fontSize: 14,
                                color: "grey",
                                marginTop: 10,
                            }}>{"Starting at: PKR " + route.params.service?.price}</Text>
                            <Text style={{
                                fontSize: 18,
                                color: "grey",
                                marginTop: 20,
                            }}>{route.params.service?.details}</Text>

                            <Button
                                type={"solid"}
                                title={"Go Back"}
                                buttonStyle={{borderColor: "#797979", borderRadius: 20}}
                                titleStyle={{color: "#ffffff"}}
                                onPress={() => {
                                    navigation.goBack()

                                }}
                                style={{marginTop: 20, marginHorizontal: 20}}
                            />


                        </ScrollView>
                    </View>
                </KeyboardAvoidingView>

            )}
        </UserContext.Consumer>
    );
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
    },

    contentContainer: {
        paddingTop: 30,
        paddingBottom: 20
    },

});
